        let proof_tau_list = Issuer::_create_tau_list_values(
            &claim_definition.clone().unwrap().data.public_key_revocation.clone().unwrap(),
            &revocation_reg.borrow().accumulator, &c_list_params, &proof_c_list).unwrap();
