        [TestMethod] 
        public async Task TestVerifierVerifyProofWorksForCorrectProof()
        {
            var schemasJson = new JObject(new JProperty(gvtSchemaId, JObject.Parse(gvtSchema))).ToString();
            var credentialDefsJson = new JObject(new JProperty(issuer1gvtCredDefId, JObject.Parse(credentialDef))).ToString();
            var revocRegDefsJson = "{}";
            var revocRegsJson = "{}";

            proofJson = proofJson.Replace("\"schema_id\": \"\"", string.Format("\"schema_id\": \"{0}\"", gvtSchemaId));
            proofJson = proofJson.Replace("\"cred_def_id\": \"\"", string.Format("\"cred_def_id\": \"{0}\"", issuer1gvtCredDefId));

            var valid = await AnonCreds.VerifierVerifyProofAsync(verifierProofRequest, proofJson, schemasJson, credentialDefsJson, revocRegDefsJson, revocRegsJson);
            Assert.IsTrue(valid);
        }
        
